import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Download, Printer, Mail, Phone, Github, Linkedin } from 'lucide-react';
import './Resume.css';

const Resume = () => {
  const [ref, inView] = useInView({ threshold: 0.1 });

  const handlePrint = () => {
    window.print();
  };

  const handleDownload = () => {
    // Import html2pdf dynamically
    const script = document.createElement('script');
    script.src = 'https://cdnjs.cloudflare.com/ajax/libs/html2pdf.js/0.10.1/html2pdf.bundle.min.js';
    script.onload = () => {
      const element = document.querySelector('.resume-content');
      const opt = {
        margin: [0.5, 0.5, 0.5, 0.5],
        filename: 'Tyler_Jay_Perkins_Resume.pdf',
        image: { type: 'jpeg', quality: 0.98 },
        html2canvas: { 
          scale: 2,
          useCORS: true,
          letterRendering: true
        },
        jsPDF: { 
          unit: 'in', 
          format: 'letter', 
          orientation: 'portrait' 
        },
        pagebreak: { 
          mode: ['avoid-all', 'css', 'legacy'],
          before: '.page-break-before',
          after: '.page-break-after'
        }
      };

      // Temporarily add PDF-specific classes
      element.classList.add('pdf-export');

      window.html2pdf().set(opt).from(element).save().then(() => {
        // Remove the temporary class
        element.classList.remove('pdf-export');
      });
    };
    document.head.appendChild(script);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <section className="resume-section" ref={ref}>
      <div className="container">
        <motion.div
          className="resume-actions no-print"
          initial={{ opacity: 0, y: -20 }}
          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: -20 }}
          transition={{ duration: 0.6 }}
        >
          <motion.button
            className="action-button"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleDownload}
          >
            <Download size={20} />
            Download PDF
          </motion.button>
          <motion.button
            className="action-button"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handlePrint}
          >
