import React, { useEffect, useRef } from 'react';
import './RainEffect.css';

const RainEffect = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width = window.innerWidth;
    let height = window.innerHeight;

    const updateCanvasSize = () => {
      const dpr = window.devicePixelRatio || 1;
      width = window.innerWidth;
      height = window.innerHeight;
      
      canvas.width = width * dpr;
      canvas.height = height * dpr;
      canvas.style.width = width + 'px';
      canvas.style.height = height + 'px';
      
      ctx.scale(dpr, dpr);
    };

    updateCanvasSize();

    // Get theme-aware colors
    const getThemeColors = () => {
      const isDark = document.documentElement.getAttribute('data-theme') === 'dark';
      
      if (isDark) {
        return {
          rain: 'rgba(142, 198, 209, 0.8)',      // Teal rain
          ripple: 'rgba(142, 198, 209, 0.6)',    // Teal ripples
          rippleCenter: 'rgba(107, 156, 165, 1)' // Darker teal center
        };
      } else {
        return {
          rain: 'rgba(244, 143, 177, 0.7)',      // Pink rain
          ripple: 'rgba(244, 143, 177, 0.5)',    // Pink ripples
          rippleCenter: 'rgba(248, 187, 217, 1)' // Lighter pink center
        };
      }
    };

    // Raindrop class
    class Raindrop {
      constructor() {
        this.x = Math.random() * width;
        this.y = Math.random() * -height - 100;
        this.length = Math.random() * 15 + 10; // Raindrop length
        this.speed = Math.random() * 8 + 4;     // Fall speed
        this.opacity = Math.random() * 0.8 + 0.2;
        this.width = Math.random() * 2 + 1;     // Raindrop thickness
      }

      update() {
        this.y += this.speed;
        
        // Check if raindrop hits the "ground" (bottom 20% of screen)
        if (this.y > height * 0.8 && !this.hasHitGround) {
          this.hasHitGround = true;
          // Create ripple at impact point
          ripples.push(new Ripple(this.x, height * 0.8 + Math.random() * height * 0.2));
        }
        
        // Reset raindrop when it goes off screen
        if (this.y > height + 50) {
          this.y = Math.random() * -200 - 100;
          this.x = Math.random() * width;
          this.hasHitGround = false;
        }
      }

      draw(ctx, colors) {
        const gradient = ctx.createLinearGradient(this.x, this.y, this.x, this.y + this.length);
        gradient.addColorStop(0, colors.rain.replace(/[\d.]+\)$/g, '0)'));
        gradient.addColorStop(1, colors.rain);
        
        ctx.strokeStyle = gradient;
        ctx.lineWidth = this.width;
        ctx.lineCap = 'round';
        
        ctx.beginPath();
        ctx.moveTo(this.x, this.y);
        ctx.lineTo(this.x, this.y + this.length);
        ctx.stroke();
      }
    }

    // Ripple class for ground impacts
    class Ripple {
      constructor(x, y) {
        this.x = x;
        this.y = y;
        this.radius = 0;
        this.maxRadius = Math.random() * 30 + 20;
        this.opacity = 1;
        this.speed = 1.5;
        this.age = 0;
        this.maxAge = 60; // Frames to live
      }

      update() {
        this.age++;
        this.radius += this.speed;
        this.opacity = 1 - (this.age / this.maxAge);
        
        // Slow down expansion as it gets bigger
        this.speed *= 0.98;
      }

      draw(ctx, colors) {
        if (this.opacity <= 0) return;
        
        // Create multiple concentric ripples for 3D effect
        for (let i = 0; i < 3; i++) {
          const rippleRadius = this.radius - (i * 8);
          if (rippleRadius > 0) {
            const rippleOpacity = this.opacity * (1 - i * 0.3);
            
            // Outer ring
            ctx.beginPath();
            ctx.arc(this.x, this.y, rippleRadius, 0, Math.PI * 2);
            ctx.strokeStyle = colors.ripple.replace(/[\d.]+\)$/g, rippleOpacity * 0.6 + ')');
            ctx.lineWidth = 2 - i * 0.5;
            ctx.stroke();
            
            // Inner filled circle for depth
            if (i === 0) {
              ctx.beginPath();
              ctx.arc(this.x, this.y, rippleRadius * 0.3, 0, Math.PI * 2);
              ctx.fillStyle = colors.rippleCenter.replace(/[\d.]+\)$/g, rippleOpacity * 0.3 + ')');
              ctx.fill();
            }
          }
        }
      }

      isDead() {
        return this.age >= this.maxAge;
      }
    }

    // Create raindrops
    const raindrops = [];
    const ripples = [];
    const raindropCount = window.innerWidth > 768 ? 150 : 80;
    
    for (let i = 0; i < raindropCount; i++) {
      raindrops.push(new Raindrop());
    }

    // Animation loop
    const animate = () => {
      // Clear canvas with slight transparency for motion blur effect
      ctx.fillStyle = 'rgba(0, 0, 0, 0.02)';
      ctx.fillRect(0, 0, width, height);
      
      const colors = getThemeColors();
      
      // Update and draw raindrops
      raindrops.forEach(drop => {
        drop.update();
        drop.draw(ctx, colors);
      });
      
      // Update and draw ripples
      for (let i = ripples.length - 1; i >= 0; i--) {
        const ripple = ripples[i];
        ripple.update();
        ripple.draw(ctx, colors);
        
        // Remove dead ripples
        if (ripple.isDead()) {
          ripples.splice(i, 1);
        }
      }

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Listen for theme changes
    const observer = new MutationObserver(() => {
      // Colors will update automatically on next frame
    });
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ['data-theme']
    });

    const handleResize = () => {
      updateCanvasSize();
      // Adjust raindrop count for new screen size
      const newCount = window.innerWidth > 768 ? 150 : 80;
      if (newCount !== raindrops.length) {
        raindrops.length = 0;
        for (let i = 0; i < newCount; i++) {
          raindrops.push(new Raindrop());
        }
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      observer.disconnect();
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  return <canvas ref={canvasRef} className="rain-effect-background" />;
};

export default RainEffect;
