import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Code, GraduationCap, Heart } from 'lucide-react';
import './About.css';

const About = () => {
  const [ref, inView] = useInView({ threshold: 0.3 });

  // Tech stack with logo URLs from devicons CDN
  const techStack = [
    { name: 'JavaScript', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/javascript/javascript-original.svg' },
    { name: 'React', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/react/react-original.svg' },
    { name: 'Python', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg' },
    { name: 'C++', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/cplusplus/cplusplus-original.svg' },
    { name: 'C', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/c/c-original.svg' },
    { name: 'C#', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/csharp/csharp-original.svg' },
    { name: 'HTML/CSS', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/html5/html5-original.svg' },
    { name: 'Git', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/git/git-original.svg' },
    { name: 'SQL', logo: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mysql/mysql-original.svg' },
  ];

  // Duplicate the array for seamless infinite scrolling
  const duplicatedTechStack = [...techStack, ...techStack, ...techStack];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <>
      <style jsx>{`
        .about {
          padding: 6rem 0;
          background: var(--bg-secondary);
        }

        .about-grid {
          display: grid;
          grid-template-columns: 2fr 1fr;
          gap: 4rem;
          align-items: start;
        }

        .about-text {
          display: flex;
          flex-direction: column;
          gap: 2rem;
        }

        .about-card {
          background: var(--bg-primary);
          padding: 2rem;
          border-radius: 20px;
          box-shadow: 0 10px 30px var(--shadow);
          transition: transform 0.3s ease;
        }

        .about-card:hover {
          transform: translateY(-5px);
        }

        .card-icon {
          color: var(--accent-primary);
          margin-bottom: 1rem;
          width: 48px;
          height: 48px;
        }

        .about-card h3 {
          font-size: 1.5rem;
          margin-bottom: 1rem;
          color: var(--text-primary);
        }

        .about-card p {
          color: var(--text-secondary);
          line-height: 1.6;
        }

        .skills-section {
          background: var(--bg-primary);
          padding: 2rem;
          border-radius: 20px;
          box-shadow: 0 10px 30px var(--shadow);
          height: fit-content;
          position: sticky;
          top: 2rem;
          overflow: hidden;
        }

        .skills-section h3 {
          font-size: 1.5rem;
          margin-bottom: 1.5rem;
          color: var(--text-primary);
          text-align: center;
        }

        .tech-scroll-container {
          width: 100%;
          overflow: hidden;
          mask: linear-gradient(
            90deg,
            transparent,
            white 20%,
            white 80%,
            transparent
          );
          -webkit-mask: linear-gradient(
            90deg,
            transparent,
            white 20%,
            white 80%,
            transparent
          );
        }

        .tech-scroll-track {
          display: flex;
          animation: scroll 20s linear infinite;
          width: fit-content;
          will-change: transform;
        }

        .tech-logo-item {
          flex: 0 0 auto;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          margin: 0 1.5rem;
          padding: 1rem;
          transition: transform 0.3s ease;
          width: 80px;
        }

        .tech-logo-item:hover {
          transform: scale(1.1);
        }

        .tech-logo {
          width: 48px;
          height: 48px;
          object-fit: contain;
          filter: grayscale(1) opacity(0.7);
          transition: all 0.3s ease;
        }

        .tech-logo-item:hover .tech-logo {
          filter: grayscale(0) opacity(1);
          transform: translateY(-2px);
        }

        .tech-name {
          font-size: 0.75rem;
          color: var(--text-secondary);
          margin-top: 0.5rem;
          text-align: center;
          font-weight: 500;
        }

        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(calc(-100% / 3));
          }
        }

        .section-title {
          font-size: 3rem;
          font-weight: 600;
          text-align: center;
          margin-bottom: 3rem;
          color: var(--text-primary);
        }

        .container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
        }

        /* Mobile Responsive */
        @media (max-width: 768px) {
          .about {
            padding: 4rem 0;
          }

          .about-grid {
            grid-template-columns: 1fr;
            gap: 2rem;
          }

          .skills-section {
            position: static;
          }

          .about-card {
            padding: 1.5rem;
          }

          .section-title {
            font-size: 2.5rem;
            margin-bottom: 2rem;
          }

          .container {
            padding: 0 1rem;
          }

          .tech-logo {
            width: 40px;
            height: 40px;
          }

          .tech-logo-item {
            margin: 0 1rem;
            min-width: 60px;
          }

          .tech-scroll-track {
            animation-duration: 15s;
          }
        }

        /* Theme-specific adjustments */
        [data-theme="dark"] .tech-logo {
          filter: grayscale(1) opacity(0.6) brightness(1.2);
        }

        [data-theme="dark"] .tech-logo-item:hover .tech-logo {
          filter: grayscale(0) opacity(1) brightness(1);
        }
      `}</style>

      <section id="about" className="about" ref={ref}>
        <div className="container">
          <motion.div
            className="about-content"
            variants={containerVariants}
            initial="hidden"
            animate={inView ? "visible" : "hidden"}
          >
            <motion.h2 className="section-title" variants={itemVariants}>
              About Me
            </motion.h2>

            <div className="about-grid">
              <motion.div className="about-text" variants={itemVariants}>
                <div className="about-card">
                  <Code className="card-icon" />
                  <h3>Passionate Developer</h3>
                  <p>
                    I love creating digital experiences that matter. From web applications 
                    to complex algorithms, I enjoy the process of turning ideas into reality 
                    through clean, efficient code.
                  </p>
                </div>

                <div className="about-card">
                  <GraduationCap className="card-icon" />
                  <h3>Computer Science Student</h3>
                  <p>
                    Currently finishing my senior year at Utah Valley University, 
                    where I've developed a strong foundation in software engineering, 
                    data structures, and system design.
                  </p>
                </div>

                <div className="about-card">
                  <Heart className="card-icon" />
                  <h3>Problem Solver</h3>
                  <p>
                    I thrive on challenges and love finding creative solutions to complex problems. 
                    Whether it's optimizing performance or designing user-friendly interfaces, 
                    I approach each project with curiosity and determination.
                  </p>
                </div>
              </motion.div>

              <motion.div className="skills-section" variants={itemVariants}>
                <h3>Technical Skills</h3>
                <div className="tech-scroll-container">
                  <div className="tech-scroll-track">
                    {duplicatedTechStack.map((tech, index) => (
                      <div key={`${tech.name}-${index}`} className="tech-logo-item">
                        <img 
                          src={tech.logo} 
                          alt={tech.name}
                          className="tech-logo"
                          onError={(e) => {
                            // Fallback if image fails to load
                            e.target.style.display = 'none';
                          }}
                        />
                        <span className="tech-name">{tech.name}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </motion.div>
            </div>
          </motion.div>
        </div>
      </section>
    </>
  );
};

export default About;
