import React, { useEffect, useRef } from 'react';
import './ParticleBackground.css';

const ParticleBackground = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const particlesRef = useRef([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width = window.innerWidth;
    let height = window.innerHeight;

    // Ensure canvas fills viewport without affecting layout
    const updateCanvasSize = () => {
      const dpr = window.devicePixelRatio || 1;
      width = window.innerWidth;
      height = window.innerHeight;

      canvas.width = width * dpr;
      canvas.height = height * dpr;
      canvas.style.width = width + 'px';
      canvas.style.height = height + 'px';

      ctx.scale(dpr, dpr);
    };

    updateCanvasSize();

    // Get theme-aware colors
    const getThemeColors = () => {
      const isDark = document.documentElement.getAttribute('data-theme') === 'dark';

      if (isDark) {
        return {
          particles: 'rgba(142, 198, 209, 0.6)', // Teal for dark theme
          connections: 'rgba(142, 198, 209, 0.2)',
          accent: 'rgba(107, 156, 165, 0.4)'
        };
      } else {
        return {
          particles: 'rgba(244, 143, 177, 0.7)', // Soft pink for light theme
          connections: 'rgba(244, 143, 177, 0.15)',
          accent: 'rgba(248, 187, 217, 0.5)'
        };
      }
    };

    class Particle {
      constructor() {
        this.x = Math.random() * width;
        this.y = Math.random() * height;
        this.vx = (Math.random() - 0.5) * 1.2;
        this.vy = (Math.random() - 0.5) * 1.2;
        this.radius = Math.random() * 3 + 1;
        this.opacity = Math.random() * 0.8 + 0.2;
        this.pulse = Math.random() * Math.PI * 2;
        this.pulseSpeed = Math.random() * 0.02 + 0.01;
      }

      update() {
        this.x += this.vx;
        this.y += this.vy;
        this.pulse += this.pulseSpeed;

        // Bounce off edges
        if (this.x < 0 || this.x > width) this.vx *= -1;
        if (this.y < 0 || this.y > height) this.vy *= -1;

        // Keep particles in bounds
        this.x = Math.max(0, Math.min(width, this.x));
        this.y = Math.max(0, Math.min(height, this.y));
      }

      draw(ctx, colors) {
        const pulsedRadius = this.radius + Math.sin(this.pulse) * 0.5;
        const pulsedOpacity = this.opacity + Math.sin(this.pulse) * 0.2;

        ctx.beginPath();
        ctx.arc(this.x, this.y, pulsedRadius, 0, Math.PI * 2);

        // Create gradient for each particle
        const gradient = ctx.createRadialGradient(
          this.x, this.y, 0,
          this.x, this.y, pulsedRadius * 2
        );
        gradient.addColorStop(0, colors.particles.replace(/[\d.]+\)$/g, pulsedOpacity + ')'));
        gradient.addColorStop(1, colors.accent.replace(/[\d.]+\)$/g, (pulsedOpacity * 0.1) + ')'));

        ctx.fillStyle = gradient;
        ctx.fill();
      }
    }

    // Create more particles for better coverage across all sections
    const particleCount = window.innerWidth > 768 ? 75 : 45;
    particlesRef.current = [];
    for (let i = 0; i < particleCount; i++) {
      particlesRef.current.push(new Particle());
    }

    const animate = () => {
      ctx.clearRect(0, 0, width, height);
      const colors = getThemeColors();

      particlesRef.current.forEach(particle => {
        particle.update();
        particle.draw(ctx, colors);
      });

      // Draw connections between nearby particles
      particlesRef.current.forEach((particle, i) => {
        for (let j = i + 1; j < particlesRef.current.length; j++) {
          const dx = particle.x - particlesRef.current[j].x;
          const dy = particle.y - particlesRef.current[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 120) {
            const opacity = (1 - distance / 120) * 0.3;
            ctx.beginPath();
            ctx.moveTo(particle.x, particle.y);
            ctx.lineTo(particlesRef.current[j].x, particlesRef.current[j].y);
            ctx.strokeStyle = colors.connections.replace(/[\d.]+\)$/g, opacity + ')');
            ctx.lineWidth = 1;
            ctx.stroke();
          }
        }
      });

      // Add some floating geometric shapes occasionally (less frequent)
      if (Math.random() < 0.002) {
        const shapes = ['triangle', 'square', 'hexagon'];
        const shape = shapes[Math.floor(Math.random() * shapes.length)];
        drawGeometricShape(ctx, colors, shape);
      }

      animationRef.current = requestAnimationFrame(animate);
    };

    const drawGeometricShape = (ctx, colors, shape) => {
      const x = Math.random() * width;
      const y = Math.random() * height;
      const size = Math.random() * 20 + 10;

      ctx.strokeStyle = colors.accent;
      ctx.lineWidth = 2;
      ctx.beginPath();

      switch(shape) {
        case 'triangle':
          ctx.moveTo(x, y - size);
          ctx.lineTo(x - size, y + size);
          ctx.lineTo(x + size, y + size);
          ctx.closePath();
          break;
        case 'square':
          ctx.rect(x - size/2, y - size/2, size, size);
          break;
        case 'hexagon':
          for (let i = 0; i < 6; i++) {
            const angle = (i * Math.PI) / 3;
            const px = x + size * Math.cos(angle);
            const py = y + size * Math.sin(angle);
            if (i === 0) ctx.moveTo(px, py);
            else ctx.lineTo(px, py);
          }
          ctx.closePath();
          break;
        default:
          // Default to circle if shape is unrecognized
          ctx.arc(x, y, size/2, 0, Math.PI * 2);
          break;
      }
      ctx.stroke();
    };

    animate();

    const handleResize = () => {
      updateCanvasSize();
    };

    // Listen for theme changes
    const observer = new MutationObserver(() => {
      // Colors will update automatically on next frame
    });
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ['data-theme']
    });

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      observer.disconnect();
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  return <canvas ref={canvasRef} className="particle-background" />;
};

export default ParticleBackground;
